        @Override
        public boolean onTouchEvent(MotionEvent event) {
        	synchronized (UpdateThread._surfaceHolder) {
	        	for(int i=0; i<graphics.size(); i++) {
	        		if(graphics.get(i).contains(event) && event.getAction()==MotionEvent.ACTION_UP) {
	        			graphics.get(i).clicked();
		        	}
	        	//}
	        	}
        	}
        	return true;
        }
        
        @Override
        public void onDraw(Canvas canvas) {
        	synchronized (UpdateThread._surfaceHolder) {
	            canvas.drawColor(Color.BLACK);
	            for (GraphicObject graphic : graphics) {
	                graphic.draw(canvas);
	            }
	            //canvas.drawText(GestureStore.print(),300,225,GraphicObject.paint);
	            canvas.drawText(string,ConsoleActivity.width*3/5,3*ConsoleActivity.width/10,GraphicObject.paint);
	        	//}
        	}
        }
     
        @Override
        public void surfaceChanged(SurfaceHolder holder, int format, int width, int height) {
        }
     
        @Override
        public void surfaceCreated(SurfaceHolder holder) {
        	thread.setRunning(true);
            thread.start();
        }
     
        @Override
        public void surfaceDestroyed(SurfaceHolder holder) {
        	boolean retry = true;
            thread.setRunning(false);
            while (retry) {
                try {
                    thread.join();
                    retry = false;
                } catch (InterruptedException e) {
                }
            }
        }
    
